import sys
sys.path.insert(0,AVX::HELPER)
sys.path.insert(0,AVX::DEPENDENCIES)
import logger_util
import json
import requests

logger = logger_util.get_logger('Symantec & Microsoft cert creation - Submit CSR')
requests.packages.urllib3.disable_warnings()
gw_source = "external"
base_url = "<%gw_url%>avxapi/"
gwkey = "<%gw_key%>"
session_id = "<%sessionId%>"
cert_id_name = <%uuids%>
success_submission = []
failed_submission = []

def submit_csr(uuid):
    try:
        url_submit_csr = base_url+"/cert-aps-submit-csr?gwkey={gwkey}&gwsource={gw_source}".format(gwkey=gwkey,gw_source=gw_source)
        payload_data = get_submit_csr_payload(uuid)
        logger.info(url_submit_csr)
        headers = {"sessionId":session_id,"Content-Type":"application/json"}
        logger.info(json.dumps(headers))
        submit_csr_response = requests.post(url_submit_csr, headers = headers, data = payload_data, verify = False)
        logger.info(submit_csr_response)
        logger.info(submit_csr_response.text)
        submit_csr_response = json.loads(submit_csr_response.text)
        if submit_csr_response.get("response").get("message") == "Csr submission triggered successfully.":
            success_submission.append({"common_name":uuid.get("common_name"),"uuid":uuid.get("uuid"),"mongo_id":uuid.get("mongo_id")})
        else:
            failed_submission.append(uuid.get("common_name"))
    except:
        failed_submission.append(uuid.get("common_name"))

def get_submit_csr_payload(uuid):
    logger.info(str(uuid))
    payload_data = {"payload": {"certificateUuid": uuid.get("uuid",""),"apsInputRequest": {"comments": "submit"}}}
    logger.info(json.dumps(payload_data))
    return json.dumps(payload_data)

if __name__ == "__main__":
    for cert in cert_id_name:
        submit_csr(cert)
    for cert in success_submission:
        AVX::LOG("CSR Submitted successfully for the certificate with common name - {cert_common_name}".format(cert_common_name=cert.get("common_name")))
    for cert in failed_submission:
        AVX::LOG("CSR Submission failed for the certificate with common name - {cert_common_name}".format(cert_common_name=cert))
    if len(success_submission):    
        AVX::OUTPUT({"uuids":success_submission})
    else:
        AVX::OUTPUT({},2)

import sys
import json
import requests
requests.packages.urllib3.disable_warnings()
sys.path.append(AVX::DEPENDENCIES)
sys.path.append(AVX::HELPER)
import logger_util
import time
logger = logger_util.get_logger('DigiCert - Creation - Workorder status')
# fqdn = "<%%>"
fqdn = "<%output%>"
fqdn = fqdn.get("fqdn","")
gw_source = "external"
base_url = "<%gw_url%>avxapi"
gwkey = "<%gw_key%>"
session_id = "<%sessionId%>"


def get_checker_payload():
	return {"payload": [{"fqdn": fqdn,"ipPorts":[],"triggerMode": "MANUAL"}]}

def get_validate_payload(ssl_id):
	return {"payload": {"_id": ssl_id,"userName":"admin","sslCheckerRequests":[{"fqdn":fqdn,"ipPorts":[],"triggerMode":"MANUAL"}],"sslValidationReportTriggerMode":"MANUAL"}}

def get_status_payload():
	return {"payload":""}

def get_report_payload():
	return {"payload":{"searchTextValue":fqdn}}

def ssl_checker_history():
	try:
		#logger.info("====================try=================")
		#logger.info("====================SSL Checker History====================")
		url_get_ssl_checker_history =  base_url+"/cert-ssl-checker-history?gwkey={gwkey}&gwsource={gw_source}".format(gwkey=gwkey,gw_source=gw_source)
		logger.info(url_get_ssl_checker_history)
		headers = {"sessionId":session_id,"Content-Type":"application/json"}
		request_status_response = requests.post(url_get_ssl_checker_history,headers=headers,data = json.dumps(get_checker_payload()),verify=False)
		logger.info(request_status_response.text)
		request_status_response = request_status_response.json()
		logger.info(json.dumps(request_status_response))
		if request_status_response["response"] not in ["null", None, ""]:
			return request_status_response["response"]
		else:
			return False
	except Exception as e:
		#logger.info("====================except=================")
		logger.info(str(e))
		AVX::LOG(str(e))

def ssl_checker_validate(ssl_id):
	try:
		#logger.info("====================try=================")
		#logger.info("====================SSL Checker Validate====================")
		url_get_ssl_checker_validate =  base_url+"/cert-ssl-checker-validate?gwkey={gwkey}&gwsource={gw_source}".format(gwkey=gwkey,gw_source=gw_source)
		logger.info(url_get_ssl_checker_validate)
		headers = {"sessionId":session_id,"Content-Type":"application/json"}
		request_status_response = requests.post(url_get_ssl_checker_validate,headers=headers,data = json.dumps(get_validate_payload(ssl_id)),verify=False)
		request_status_response = request_status_response.json()
		logger.info(json.dumps(request_status_response))
		if request_status_response["response"] in ["null", None, ""]:
			return True
		else:
			return False
	except Exception as e:
		#logger.info("====================except=================")
		logger.info(str(e))
		AVX::LOG(str(e))

def ssl_checker_get_status():
	try:
		status = None
		#logger.info("====================try=================")
		#logger.info("====================SSL Checker Status====================")
		url_get_ssl_checker_status =  base_url+"/cert-ssl-checker-get-status?gwkey={gwkey}&gwsource={gw_source}".format(gwkey=gwkey,gw_source=gw_source)
		logger.info(url_get_ssl_checker_status)
		headers = {"sessionId":session_id,"Content-Type":"application/json"}
		while status not in [True, False]:
			request_status_response = requests.post(url_get_ssl_checker_status,headers=headers,data = json.dumps(get_status_payload()),verify=False)
			request_status_response = request_status_response.json()
			logger.info(json.dumps(request_status_response))
			if request_status_response["response"] == "Completed":
				status = True
			elif request_status_response["response"] == "InProgress":
				time.sleep(5)
			else:
				status = False
		return status
	except Exception as e:
		#logger.info("====================except=================")
		logger.info(str(e))
		AVX::LOG(str(e))

def ssl_checker_get_report():
	try:
		#logger.info("====================try=================")
		#logger.info("====================SSL Checker Grid====================")
		url_get_ssl_checker_report =  base_url+"/cert-ssl-checker-report-grid?gwkey={gwkey}&gwsource={gw_source}".format(gwkey=gwkey,gw_source=gw_source)
		logger.info(url_get_ssl_checker_report)
		headers = {"sessionId":session_id,"Content-Type":"application/json"}
		request_status_response = requests.post(url_get_ssl_checker_report,headers=headers,data = json.dumps(get_report_payload()),verify=False)
		request_status_response = request_status_response.json()
		logger.info(json.dumps(request_status_response))
		if request_status_response["response"]["data"] not in ["null",None,"",[],{}]:
			return request_status_response["response"]
		else:
			return False
	except Exception as e:
		#logger.info("====================except=================")
		logger.info(str(e))
		AVX::LOG(str(e))
if __name__ == '__main__':
	ssl_id = ssl_checker_history()
	if ssl_id:
		validation = ssl_checker_validate(ssl_id)
		if validation:
			status = ssl_checker_get_status()
			logger.info(str(status))
			if status:
				grid_status = ssl_checker_get_report()
				logger.info(str(grid_status))
				if grid_status:
					AVX::LOG(json.dumps(grid_status))
				else:
					#logger.info("===================else 1 =================")
					AVX::LOG("Failed while validating the certificate")
			else:
				#logger.info("===================else 2 =================")
				AVX::LOG("Failed while validating the certificate")
		else:
			#logger.info("===================else 3 =================")
			AVX::LOG("Failed while validating the certificate")
	else:
		#logger.info("===================else 4 =================")
		AVX::LOG("Failed while validating the certificate")
	AVX::OUTPUT({})
