db = connect_db.appviewx
null = None
null_list = ["None",None,"null",""," "]
device = "<%dd_device%>"
vip_name = "<%dd_vip_name%>"
vip_ip = "<%txt_vip_ip%>"
vip_port = "<%txt_vip_port%>"
pool_name = "<%txt_pool_name%>".split("/")[2]
client_ssl = "<%dd_existing_client_ssl%>"
server_ssl = "<%dd_existing_server_ssl%>"
http_profile = "<%dd_existing_http_profile%>"
persistence = "<%dd_persistence%>"
snat = "<%dd_snat_opt%>"
snat_pool = "<%dd_snat_pool_name%>"
monitors = "<%tab_monitors%>"
irules = "<%tab_irule%>"
