is_bash = "<%is_bash%>"
commands = []


def fetchDeviceID(devicename):
    '''
    function to fetch the device id from the database
    Params:
    @str - devicename
    Return:
    @str - DeviceId
    '''
    logger.info("Inside fetchDeviceID() with parameter" + str(devicename))
    collection = connect_db.appviewx.device
    dev_obj=collection.find_one({"name": re.compile(devicename, re.IGNORECASE)})
    return str(dev_obj['_id'])


def get_persistance_profile(device_id,vip_name):
    '''
    Function to fetch the list of available persistence profile for the VIP
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    Return:
    @list - Available persistence profile in the VIP
    '''
    logger.info("Inside Function get_persistance_profile() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))
    collection = db.me_adc
    persistences = collection.find_one({"deviceId":device_id,"code":"vs","name":vip_name,"partition":"Common"},{"ltmpersistence":1})["ltmpersistence"]
    # raise ValueError(str(persistences))
    for persistance in persistences:
        persist = collection.find_one({"_id":persistance,"deviceId":device_id,"code":"ltmpersistence","partition":"Common"},{"type":1,"name":1})
        if persist["type"] == "Persistence":
            if persist["name"]:
                return persist["name"]
            else:
                return "None"



def get_client_server_ssl_profile(device_id,vip_name):
    '''
    Function to get the list of all profiles (client ssl, server ssl and http) associated to the VIP
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    Return:
    @dict - Available profile in the VIP
    '''
    logger.info("Inside Function get_client_server_ssl_profile() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))

    collection = db.me_adc
    prof_list = {}
    profiles = collection.find_one({"deviceId":device_id,"code":"vs","name":vip_name,"partition":"Common"},{"profiles":1})["profiles"]
    for profile in profiles:
        prof_name = profile.split("/")[2]
        profile_type = collection.find_one({"deviceId":device_id,"code":"lpr","name":prof_name,"partition":"Common"},{"type":1})["type"]
        if profile_type == "server-ssl":
            prof_list["old_client_ssl"] = prof_name
        elif profile_type == "client-ssl":
            prof_list["old_server_ssl"] = prof_name
        elif profile_type == "http":
            prof_list["old_http_profile"] = prof_name
        else:
            pass
    return prof_list


def get_profile_change_commands(device_id,vip_name):
    '''
    Function to get the list to changes the profile into the new value
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    Return:
    @list - Dict with the change parameters
    '''
    logger.info("Inside Function get_profile_change_commands() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))
    profile_old = get_client_server_ssl_profile(device_id,vip_name)
    
    prof_change_list = []
    if client_ssl != profile_old.get("old_client_ssl","") and client_ssl not in null_list:
        prof_change_list.append({"type":"clientssl","changes":True,"old":profile_old.get("old_client_ssl",""),"new":client_ssl})
    elif client_ssl != profile_old.get("old_client_ssl","") and client_ssl == "None":
        prof_change_list.append({"type":"clientssl","changes":True,"old":profile_old.get("old_client_ssl",""),"new":client_ssl})
    else:
        prof_change_list.append({"type":"clientssl","changes":False})
    if server_ssl != profile_old.get("old_server_ssl","") and server_ssl not in null_list:
        prof_change_list.append({"type":"serverssl","changes":True,"old":profile_old.get("old_server_ssl",""),"new":server_ssl})
    elif server_ssl != profile_old.get("old_server_ssl","") and server_ssl == "None":
        prof_change_list.append({"type":"serverssl","changes":True,"old":profile_old.get("old_server_ssl",""),"new":server_ssl})
    else:
        prof_change_list.append({"type":"serverssl","changes":False})
    if http_profile != profile_old.get("old_http_profile","") and http_profile not in null_list:
        prof_change_list.append({"type":"http","changes":True,"old":profile_old.get("old_http_profile",""),"new":http_profile})
    elif http_profile != profile_old.get("old_http_profile","") and http_profile == "None":
        prof_change_list.append({"type":"http","changes":True,"old":profile_old.get("old_http_profile",""),"new":http_profile})
    else:
        prof_change_list.append({"type":"http","changes":False})
    return prof_change_list

def generate_profile_commands(device_id):
    '''
    Function to get the list of commands to change the profile into the new value
    Params:
    @str - device_id - Id of the device
    '''
    logger.info("Inside Function generate_profile_commands() with params: device_id"+str(device_id))
    profile_changes = get_profile_change_commands(device_id,vip_name)
    base_cmd = "modify ltm virtual " + vip_name
    change = ""
    prof_command = ""
    del_list = []
    add_list = []
    for profile in profile_changes:
        if profile["changes"]:
            change = "yes"
            logger.info("Changes in profile type "+str(profile["type"]))
            if profile["new"] not in null_list:
                add_list.append(profile["new"])
            if profile["old"] not in null_list:
                del_list.append(profile["old"])
        else:
            logger.info("No changes in profile type "+str(profile["type"]))

    if change == "yes":
        logger.info("Changes in Profile detected. Framing TMOS Commands")
        if del_list:
            prof_command += base_cmd + " profiles delete { " + " ".join(del_list)+" }"
        if add_list:
            if del_list:
                prof_command += " profiles add { "+" ".join(add_list)+" }"
            else:
                prof_command += base_cmd + " profiles add { "+" ".join(add_list)+" }"
        if add_list or del_list:
            commands.append(prof_command)
    else:
        logger.info("NO changes in profiles.")

def generate_modify_irule_commands():
    '''
    Function to get the list of commands to modify the iRules for the VIP
    '''
    logger.info("Inside the Function generate_modify_irule_commands() with no params")
    cmd = "modify ltm virtual " + vip_name + " rules "
    irule_list = [row['dd_irules'] for index, row in enumerate(irules) if row['action'] != 'delete']
    irule_list_del = [row['dd_irules'] for index, row in enumerate(irules) if row['action'] == 'delete']
    if irule_list:
        cmd = cmd + '{ ' + ' '.join(irule_list) + ' }'
        commands.append(cmd)
    elif len(irule_list_del) != 0 and len(irule_list) == 0:
        commands.append(cmd + "  none")

def modify_persistent(device_id,vip_name):
    '''
    Function to get the commands for the persistence
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    '''
    logger.info("Inside Function modify_persistent() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))
    persist = get_persistance_profile(device_id,vip_name)

    if persistence != persist and persistence not in null_list:
        commands.append("modify ltm virtual " + vip_name + " persist replace-all-with { " + persistence + " }")
    elif persist != persistence and persistence == "None":
        commands.append("modify ltm virtual " + vip_name + " persist none")

def get_snat_details(device_id,vip_name):
    '''
    Function to get the details of the SNAT Value of the VIP
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    Return:
    @str - snat type
    '''
    logger.info("Inside Function get_snat_details() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))
    collection = db.me_adc
    snat = collection.find_one({"deviceId":device_id,"code":"vs","name":vip_name,"partition":"Common"},{"snat":1})["snat"]
    if snat == "none":
        snat = "None"
    elif snat == "automap":
        snat = "automap"
    return snat


def generate_snat_commands(device_id,vip_name):
    '''
    Function to get the commadns to change the Value of the VIP
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    '''
    logger.info("Inside Function generate_snat_commands() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))

    snat_old = get_snat_details(device_id,vip_name)
    try:
        if snat_old != snat and snat in ["None","automap"]:
            commands.append("modify ltm virtual "+vip_name+" snat "+snat.lower())
        elif snat_old != snat and snat not in ["None","automap"]:
            if ":@" in snat_old:
                commands.append("modify ltm virtual "+vip_name+" snatpool "+ snat_pool.split(":@")[-2])
            else:
                commands.append("modify ltm virtual "+vip_name+" snatpool "+ snat_pool)
    except Exception as e:
        logger.info(e)

def get_monitors(device_id,vip_name):
    '''
    Function to get the list of all available monitors on the VIP
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    Return:
    @list - All existing Monitors
    '''
    logger.info("Inside Function get_monitors() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))
    collection = db.me_adc
    monitors_list_old = []
    monitors = collection.find_one({"deviceId":device_id,"code":"lp","name":pool_name,"partition":"Common"},{"monitorAll":1})
    for monitor in monitors["monitorAll"]:
        monitors_list_old.append(monitor.split("/")[-1])
    return monitors_list_old

def generate_monitor_commands(device_id,vip_name):
    '''
    Function to generate the monitor commands for the VIP
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    Return:
    @list - All existing Monitors
    '''
    logger.info("Inside Function generate_monitor_commands() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))
    monitors_list =[]
    if pool_name not in null_list:
        monitor = ''
        old_monitors = get_monitors(device_id,vip_name)
        for index, row in enumerate(monitors):
            if row['dd_existing_monitors'] not in old_monitors:
                if row['action'] == 'add':
                    monitors_list.append(row['dd_existing_monitors'])
                if row['action'] == 'modify':
                    monitors_list.append(row['dd_existing_monitors'])
                if row['action'] == '':
                    monitors_list.append(row['dd_existing_monitors'])
        for i in range(len(monitors_list)):
            if i == 0:
                monitor = monitor + monitors_list[i]
            else:
                monitor = monitor + ' and ' + monitors_list[i]
        if len(monitors_list) != 0:
            commands.append("modify ltm pool " + pool_name + " monitor "+ monitor)
        else:
            commands.append("modify ltm pool " + pool_name + " monitor none")


def modify_pool_member(device_id,vip_name):
    '''
    Function to generate the commands for modify the pool members of the pool associated to the VIP
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    Return:
    @list - All existing Monitors
    '''
    logger.info("Inside Function modify_pool_member() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))
    existing_member = []
    existing_ratio = []
    if pool_name not in null_list:
        try:
            collection = db.me_adc
            lbmode_old = collection.find_one({"name":pool_name,"deviceId":device_id,"code":"lp","partition":"Common"},{"lbmode":1})["lbmode"]
        except Exception as e:
            pass

        if lbmode_old != lb_mode and lb_mode not in null_list:
            commands.append("modify ltm pool " + pool_name + " load-balancing-mode " + lb_mode)
        for member in collection.find({"name": pool_name, "deviceId": device_id, "code": "lp", "partition": "Common"}):
            if member:
                lpm=member["lpm"]
                for each in lpm:                    
                    for mem in collection.find({"_id": each.strip()}):
                        existing_member.append(mem["ip"] + ':' + mem["port"])
                        existing_ratio.append(mem["ratio"])
        if len(existing_member)<len(pool_members):
            for i in range(len(existing_member),len(pool_members)):
                existing_member.append( "0.0.0.0:00" )
                existing_ratio.append("0")

        for index, row in enumerate(pool_members):
            ip_port = row['txt_pool_member_ip'] + ':' + row['txt_pool_member_port']
            if row['action'] == 'modify':
                if ip_port in existing_member and existing_ratio[index] != row['txt_pool_member_ratio']:
                    commands.append("modify ltm pool " + pool_name + " members modify { " + ip_port + " {ratio " + row['txt_pool_member_ratio'] + "}}")
                elif ip_port not in existing_member:
                    ip = existing_member[index].split(':')[0]
                    port = existing_member[index].split(':')[1]
                    value = collection.find_one({"parentName": pool_name, "deviceId": device_id, "code": "lpm", "partition": "Common",'ip':ip,'port':port},{'name':1})
                    if existing_member[index]!= "0.0.0.0:00":
                        commands.append("modify ltm pool " + pool_name + " members delete { " + value['name'] + " }")
                if lb_mode == "ratio-member":
                    if ip_port not in existing_member:
                        commands.append("modify ltm pool " + pool_name + " members add { " + ip_port + " {ratio " + row['txt_pool_member_ratio'] + "}}")
                else:
                    commands.append("modify ltm pool " + pool_name + " members add { " + ip_port + " }")
            elif ip_port not in existing_member and row['action'] != 'delete':
                if lb_mode == 'ratio-member':
                    commands.append("modify ltm pool " + pool_name + " members add { " + ip_port + " {ratio " + row['txt_pool_member_ratio'] + "}}")
                else:
                    commands.append("modify ltm pool " + pool_name + " members add { " + ip_port + " }")
            elif row['action'] == 'delete':
                ip = existing_member[index].split(':')[0]
                port = existing_member[index].split(':')[1]
                value = collection.find_one({"parentName": pool_name, "deviceId": device_id, "code": "lpm", "partition": "Common",'ip':ip,'port':port},{'name':1})
                if value:
                    commands.append("modify ltm pool " + pool_name + " members delete { " + value['name'] + " }")

if __name__ == '__main__':
    '''
    main
    '''
    device_id = fetchDeviceID(device)
    if is_bash == "bash":
