    commands.append("create cli transaction")
    generate_profile_commands(device_id)
    modify_persistent(device_id,vip_name)
    generate_snat_commands(device_id,vip_name)
    generate_modify_irule_commands()
    generate_monitor_commands(device_id,vip_name)
    modify_pool_member(device_id,vip_name)
    
    commands.append("submit cli transaction")
    
    for cmd in commands:
        logger.info(cmd)
        AVX::CMD(device+":@"+cmd)
    AVX::CMD(device+":@save sys config")
    AVX::CMD(device+":@quit")
    config = {}
    config["implementation"] = AVX::CONFIG
    AVX::LOG("Configurations Generated Successfully")
    AVX::OUTPUT(config)

