def modify_pool_member(device_id,vip_name):
    '''
    Function to generate the commands for modify the pool members of the pool associated to the VIP
    Params:
    @str - device_id - Id of the device
    @str - vip_name - Name of the VIP
    Return:
    @list - All existing Monitors
    '''
    logger.info("Inside Function modify_pool_member() with params: device_id"+str(device_id)+" vip_name "+str(vip_name))
    existing_member = []
    existing_ratio = []
    if pool_name not in null_list:
        try:
            collection = db.me_adc
            lbmode_old = collection.find_one({"name":pool_name,"deviceId":device_id,"code":"lp","partition":"Common"},{"lbmode":1})["lbmode"]
        except Exception as e:
            pass

        if lbmode_old != lb_mode and lb_mode not in null_list:
            commands.append("modify ltm pool " + pool_name + " load-balancing-mode " + lbmode_old)
        for member in collection.find({"name": pool_name, "deviceId": device_id, "code": "lp", "partition": "Common"}):
            if member:
                lpm=member["lpm"]
                for each in lpm:                    
                    for mem in collection.find({"_id": each.strip()}):
                        existing_member.append(mem["ip"] + ':' + mem["port"])
                        existing_ratio.append(mem["ratio"])
        if len(existing_member)<len(pool_members):
            for i in range(len(existing_member),len(pool_members)):
                existing_member.append( "0.0.0.0:00" )
                existing_ratio.append("0")

        for index, row in enumerate(pool_members):
            ip_port = row['txt_pool_member_ip'] + ':' + row['txt_pool_member_port']
            if row['action'] == 'modify':
                if ip_port in existing_member and existing_ratio[index] != row['txt_pool_member_ratio']:
                    commands.append("modify ltm pool " + pool_name + " members modify { " + ip_port + " {ratio " + row['txt_pool_member_ratio'] + "}}")
                elif ip_port not in existing_member:
                    ip = existing_member[index].split(':')[0]
                    port = existing_member[index].split(':')[1]
                    value = collection.find_one({"parentName": pool_name, "deviceId": device_id, "code": "lpm", "partition": "Common",'ip':ip,'port':port},{'name':1})
                    if existing_member[index]!= "0.0.0.0:00":
                        commands.append("modify ltm pool " + pool_name + " members add { " + value['name'] + " }")
                if lb_mode == "ratio-member":
                    if ip_port not in existing_member:
                        commands.append("modify ltm pool " + pool_name + " members delete { " + ip_port + " }")
                else:
                    commands.append("modify ltm pool " + pool_name + " members delete { " + ip_port + " }")
            elif ip_port not in existing_member and row['action'] != 'delete':
                if lb_mode == 'ratio-member':
                    commands.append("modify ltm pool " + pool_name + " members delete { " + ip_port  + " }")
                else:
                    commands.append("modify ltm pool " + pool_name + " members delete { " + ip_port + " }")
            elif row['action'] == 'delete':
                ip = existing_member[index].split(':')[0]
                port = existing_member[index].split(':')[1]
                value = collection.find_one({"parentName": pool_name, "deviceId": device_id, "code": "lpm", "partition": "Common",'ip':ip,'port':port},{'name':1})
                if value:
                    commands.append("modify ltm pool " + pool_name + " members add { " + value['name'] + " }")

